/**
 * Flexible container component for grouping related content with semantic structure
 *
 * @registry-name card
 * @registry-version 0.1.0
 * @registry-status published
 * @registry-path components/ui/Card.tsx
 * @registry-type registry:component
 *
 * @cognitive-load 2/10 - Simple container with clear boundaries and minimal cognitive overhead
 * @attention-economics Neutral container: Content drives attention, elevation hierarchy for interactive states
 * @trust-building Consistent spacing, predictable interaction patterns, clear content boundaries
 * @accessibility Proper heading structure, landmark roles, keyboard navigation for interactive cards
 * @semantic-meaning Structural roles: article=standalone content, section=grouped content, aside=supplementary information
 *
 * @usage-patterns
 * DO: Group related information with clear visual boundaries
 * DO: Create interactive cards with hover states and focus management
 * DO: Establish information hierarchy with header, content, actions
 * DO: Implement responsive scaling with consistent proportions
 * NEVER: Use decorative containers without semantic purpose
 *
 * @design-guides
 * - Content Grouping: https://rafters.realhandy.tech/docs/llm/content-grouping
 * - Attention Economics: https://rafters.realhandy.tech/docs/llm/attention-economics
 * - Spatial Relationships: https://rafters.realhandy.tech/docs/llm/spatial-relationships
 *
 * @dependencies @rafters/design-tokens/motion
 *
 * @example
 * ```tsx
 * // Basic card with content structure
 * <Card>
 *   <CardHeader>
 *     <CardTitle>Card Title</CardTitle>
 *     <CardDescription>Supporting description</CardDescription>
 *   </CardHeader>
 *   <CardContent>
 *     Main card content
 *   </CardContent>
 *   <CardFooter>
 *     <Button>Action</Button>
 *   </CardFooter>
 * </Card>
 * ```
 */
import { contextEasing, contextTiming } from '@rafters/design-tokens/motion';
import { cn } from '../lib/utils';

export interface CardProps extends React.HTMLAttributes<HTMLDivElement> {
  /** Cognitive load: Card density for information hierarchy */
  density?: 'compact' | 'comfortable' | 'spacious';
  /** Cognitive load: Interaction affordance */
  interactive?: boolean;
  /** Scanability: Visual prominence for important cards */
  prominence?: 'subtle' | 'default' | 'elevated';
  ref?: React.Ref<HTMLDivElement>;
}

export function Card({
  className,
  density = 'comfortable',
  interactive = false,
  prominence = 'default',
  ref,
  ...props
}: CardProps) {
  return (
    <div
      ref={ref}
      className={cn(
        'rounded-lg border bg-card text-card-foreground transition-all',
        contextTiming.hover,
        contextEasing.hover,
        // Cognitive load: Information density controls
        {
          'border-border shadow-sm': prominence === 'subtle',
          'border-border shadow-md': prominence === 'default',
          'border-border shadow-lg': prominence === 'elevated',
        },
        // Interaction affordance: Clear hover states for interactive cards
        interactive &&
          'cursor-pointer hover:shadow-md hover:border-accent-foreground/20 hover:scale-[1.02] active:scale-[0.98]',
        // Motor accessibility: Ensure adequate touch targets for interactive cards
        interactive && 'min-h-[44px]',
        className
      )}
      role={interactive ? 'button' : undefined}
      tabIndex={interactive ? 0 : undefined}
      {...props}
    />
  );
}

export interface CardHeaderProps extends React.HTMLAttributes<HTMLDivElement> {
  /** Cognitive load: Header density for information hierarchy */
  density?: 'compact' | 'comfortable' | 'spacious';
  ref?: React.Ref<HTMLDivElement>;
}

export function CardHeader({ className, density = 'comfortable', ref, ...props }: CardHeaderProps) {
  return (
    <div
      ref={ref}
      className={cn(
        'flex flex-col space-y-1.5',
        // Cognitive load: Adaptive spacing based on information density
        {
          'p-4': density === 'compact',
          'p-6': density === 'comfortable',
          'p-8': density === 'spacious',
        },
        className
      )}
      {...props}
    />
  );
}

export interface CardTitleProps extends React.HTMLAttributes<HTMLHeadingElement> {
  /** Information hierarchy: Semantic heading level */
  level?: 1 | 2 | 3 | 4 | 5 | 6;
  /** Scanability: Visual weight for content hierarchy */
  weight?: 'normal' | 'medium' | 'semibold';
  ref?: React.Ref<HTMLHeadingElement>;
}

export function CardTitle({
  className,
  level = 3,
  weight = 'semibold',
  ref,
  ...props
}: CardTitleProps) {
  const HeadingTag = `h${level}` as 'h1' | 'h2' | 'h3' | 'h4' | 'h5' | 'h6';

  return (
    <HeadingTag
      ref={ref}
      className={cn(
        'leading-none tracking-tight',
        // Information hierarchy: Semantic sizing based on heading level
        {
          'text-3xl': level === 1,
          'text-2xl': level === 2,
          'text-xl': level === 3,
          'text-lg': level === 4,
          'text-base': level === 5,
          'text-sm': level === 6,
        },
        // Scanability: Visual weight options
        {
          'font-normal': weight === 'normal',
          'font-medium': weight === 'medium',
          'font-semibold': weight === 'semibold',
        },
        className
      )}
      {...props}
    />
  );
}

export interface CardDescriptionProps extends React.HTMLAttributes<HTMLParagraphElement> {
  /** Cognitive load: Text length awareness for readability */
  truncate?: boolean;
  /** Information hierarchy: Subtle vs prominent descriptions */
  prominence?: 'subtle' | 'default';
  ref?: React.Ref<HTMLParagraphElement>;
}

export function CardDescription({
  className,
  truncate = false,
  prominence = 'default',
  ref,
  ...props
}: CardDescriptionProps) {
  return (
    <p
      ref={ref}
      className={cn(
        'text-sm leading-relaxed',
        // Cognitive load: Truncation for long descriptions
        truncate && 'line-clamp-2',
        // Information hierarchy: Prominence levels
        {
          'text-muted-foreground/70': prominence === 'subtle',
          'text-muted-foreground': prominence === 'default',
        },
        className
      )}
      {...props}
    />
  );
}

export interface CardContentProps extends React.HTMLAttributes<HTMLDivElement> {
  /** Cognitive load: Content density for information hierarchy */
  density?: 'compact' | 'comfortable' | 'spacious';
  /** Scanability: Content organization patterns */
  layout?: 'default' | 'grid' | 'list';
  ref?: React.Ref<HTMLDivElement>;
}

export function CardContent({
  className,
  density = 'comfortable',
  layout = 'default',
  ref,
  ...props
}: CardContentProps) {
  return (
    <div
      ref={ref}
      className={cn(
        'pt-0',
        // Cognitive load: Adaptive spacing
        {
          'p-4': density === 'compact',
          'p-6': density === 'comfortable',
          'p-8': density === 'spacious',
        },
        // Scanability: Layout patterns
        {
          '': layout === 'default',
          'grid grid-cols-1 gap-4': layout === 'grid',
          'space-y-3': layout === 'list',
        },
        className
      )}
      {...props}
    />
  );
}

export interface CardFooterProps extends React.HTMLAttributes<HTMLDivElement> {
  /** Cognitive load: Footer density and action clarity */
  density?: 'compact' | 'comfortable' | 'spacious';
  /** Scanability: Action hierarchy in footer */
  justify?: 'start' | 'center' | 'end' | 'between';
  ref?: React.Ref<HTMLDivElement>;
}

export function CardFooter({
  className,
  density = 'comfortable',
  justify = 'start',
  ref,
  ...props
}: CardFooterProps) {
  return (
    <div
      ref={ref}
      className={cn(
        'flex items-center pt-0',
        // Cognitive load: Adaptive spacing
        {
          'p-4': density === 'compact',
          'p-6': density === 'comfortable',
          'p-8': density === 'spacious',
        },
        // Scanability: Action layout
        {
          'justify-start': justify === 'start',
          'justify-center': justify === 'center',
          'justify-end': justify === 'end',
          'justify-between': justify === 'between',
        },
        className
      )}
      {...props}
    />
  );
}

/**
 * Notification chip component for status indicators and count overlays
 *
 * @registry-name chip
 * @registry-version 0.1.0
 * @registry-status published
 * @registry-path components/ui/Chip.tsx
 * @registry-type registry:component
 *
 * @cognitive-load 5/10 - High visibility overlay requiring immediate attention (varies by variant)
 * @attention-economics Secondary overlay with maximum visibility without overwhelming primary content
 * @trust-building Critical status and count information builds user awareness and system transparency
 * @accessibility High contrast indicators, screen reader announcements, keyboard navigation support
 * @semantic-meaning Status communication: count=quantity indication, status=state indication, badge=feature marking, dot=simple presence indicator
 *
 * @usage-patterns
 * DO: Use for notification counts (unread messages, alerts, status updates)
 * DO: Provide status indicators (live, new, beta, premium features)
 * DO: Create urgent overlays that break component boundaries for maximum visibility
 * DO: Attach universally to buttons, cards, avatars, badges, any component
 * NEVER: Use for primary actions, complex information, or standalone content
 *
 * @design-guides
 * - Attention Economics: https://rafters.realhandy.tech/docs/llm/attention-economics
 * - Notification Intelligence: https://rafters.realhandy.tech/docs/llm/notification-intelligence
 * - Trust Building: https://rafters.realhandy.tech/docs/llm/trust-building
 *
 * @dependencies class-variance-authority, clsx
 *
 * @example
 * ```tsx
 * // Notification count chip
 * <div className="relative">
 *   <Button>Messages</Button>
 *   <Chip variant="count" position="top-right">
 *     3
 *   </Chip>
 * </div>
 * 
 * // Status indicator chip
 * <div className="relative">
 *   <Badge>Premium</Badge>
 *   <Chip variant="dot" position="top-right" color="success" />
 * </div>
 * ```
 */
 * Accessibility: Full WCAG AAA compliance with screen reader count announcements
 */
import { forwardRef } from 'react';
import { cn } from '../lib/utils';

export type ChipVariant = 'urgent' | 'new' | 'live' | 'beta' | 'premium' | 'count';
export type ChipPosition = 'top-right' | 'top-left' | 'bottom-right' | 'bottom-left';
export type ChipSize = 'sm' | 'md' | 'lg';

export interface ChipProps extends React.HTMLAttributes<HTMLSpanElement> {
  // Core variant for semantic meaning
  variant: ChipVariant;

  // Positioning relative to parent component
  position?: ChipPosition;

  // Custom display value (numbers, text, icons)
  value?: string | number;

  // Size adaptation for parent component context
  size?: ChipSize;

  // Enhanced accessibility
  'aria-label'?: string;
}

// High visibility chip intelligence with attention economics
const CHIP_INTELLIGENCE = {
  urgent: {
    color: 'bg-destructive',
    textColor: 'text-destructive-foreground',
    cognitiveLoad: 9,
    attentionWeight: 'maximum',
    psychology: 'immediate_action_required',
    ariaLabel: 'Urgent notification',
  },
  new: {
    color: 'bg-primary',
    textColor: 'text-primary-foreground',
    cognitiveLoad: 6,
    attentionWeight: 'high',
    psychology: 'discovery_excitement',
    ariaLabel: 'New feature or content',
  },
  live: {
    color: 'bg-success',
    textColor: 'text-success-foreground',
    cognitiveLoad: 7,
    attentionWeight: 'high',
    psychology: 'real_time_awareness',
    ariaLabel: 'Live status indicator',
  },
  beta: {
    color: 'bg-warning',
    textColor: 'text-warning-foreground',
    cognitiveLoad: 4,
    attentionWeight: 'medium',
    psychology: 'experimental_caution',
    ariaLabel: 'Beta feature indicator',
  },
  premium: {
    color: 'bg-accent',
    textColor: 'text-accent-foreground',
    cognitiveLoad: 5,
    attentionWeight: 'medium',
    psychology: 'value_proposition',
    ariaLabel: 'Premium feature indicator',
  },
  count: {
    color: 'bg-destructive',
    textColor: 'text-destructive-foreground',
    cognitiveLoad: 8,
    attentionWeight: 'high',
    psychology: 'quantified_urgency',
    ariaLabel: 'Notification count',
  },
} as const;

export const Chip = forwardRef<HTMLSpanElement, ChipProps>(
  (
    {
      variant,
      position = 'top-right',
      value,
      size = 'md',
      className,
      'aria-label': ariaLabel,
      ...props
    },
    ref
  ) => {
    const chipInfo = CHIP_INTELLIGENCE[variant];
    const displayValue = value || (variant === 'count' ? '1' : '');

    return (
      <span
        ref={ref}
        className={cn(
          // High visibility positioning that breaks parent boundaries
          'absolute z-10 rounded-full font-bold leading-none',
          'min-w-5 h-5 flex items-center justify-center',
          'border-2 border-background', // Creates separation from parent
          chipInfo.color,
          chipInfo.textColor,

          // Position-specific styles (ALL break boundaries with -2px)
          {
            '-top-2 -right-2': position === 'top-right',
            '-top-2 -left-2': position === 'top-left',
            '-bottom-2 -right-2': position === 'bottom-right',
            '-bottom-2 -left-2': position === 'bottom-left',
          },

          // Size responsive
          {
            'text-xs min-w-4 h-4': size === 'sm',
            'text-xs min-w-5 h-5': size === 'md',
            'text-sm min-w-6 h-6': size === 'lg',
          },

          className
        )}
        aria-label={ariaLabel || `${chipInfo.ariaLabel}${value ? `: ${value}` : ''}`}
        aria-hidden="false" // Important for screen readers to announce counts
        {...props}
      >
        {displayValue}
      </span>
    );
  }
);

Chip.displayName = 'Chip';

/**
 * Modal dialog component with focus management and escape patterns
 *
 * @registry-name dialog
 * @registry-version 0.1.0
 * @registry-status published
 * @registry-path components/ui/Dialog.tsx
 * @registry-type registry:component
 *
 * @cognitive-load 6/10 - Interrupts user flow, requires decision making
 * @attention-economics Attention capture: modal=full attention, drawer=partial attention, popover=contextual attention
 * @trust-building Clear close mechanisms, confirmation for destructive actions, non-blocking for informational content
 * @accessibility Focus trapping, escape key handling, backdrop dismissal, screen reader announcements
 * @semantic-meaning Usage patterns: modal=blocking workflow, drawer=supplementary, alert=urgent information
 *
 * @usage-patterns
 * DO: Low trust - Quick confirmations, save draft (size=sm, minimal friction)
 * DO: Medium trust - Publish content, moderate consequences (clear context)
 * DO: High trust - Payments, significant impact (detailed explanation)
 * DO: Critical trust - Account deletion, permanent loss (progressive confirmation)
 * NEVER: Routine actions, non-essential interruptions
 *
 * @design-guides
 * - Trust Building: https://rafters.realhandy.tech/docs/llm/trust-building
 * - Cognitive Load: https://rafters.realhandy.tech/docs/llm/cognitive-load
 * - Progressive Enhancement: https://rafters.realhandy.tech/docs/llm/progressive-enhancement
 *
 * @dependencies @radix-ui/react-dialog, @rafters/design-tokens/motion
 *
 * @example
 * ```tsx
 * // Critical trust dialog with confirmation
 * <Dialog>
 *   <DialogTrigger asChild>
 *     <Button variant="destructive">Delete Account</Button>
 *   </DialogTrigger>
 *   <DialogContent trustLevel="critical" destructive>
 *     <DialogTitle>Delete Account</DialogTitle>
 *     <DialogDescription>This action cannot be undone.</DialogDescription>
 *   </DialogContent>
 * </Dialog>
 * ```
 */
import * as DialogPrimitive from '@radix-ui/react-dialog';
import { contextEasing, contextTiming } from '@rafters/design-tokens/motion';
import { cn } from '../lib/utils';

export interface DialogProps {
  // Trust-building intelligence
  trustLevel?: 'low' | 'medium' | 'high' | 'critical';
  destructive?: boolean;
  requireConfirmation?: boolean;

  // Cognitive load optimization
  cognitiveComplexity?: 'simple' | 'moderate' | 'complex';

  // Size variants for content hierarchy
  size?: 'sm' | 'md' | 'lg' | 'xl' | 'full';
}

export interface DialogOverlayProps
  extends React.ComponentPropsWithoutRef<typeof DialogPrimitive.Overlay> {
  ref?: React.Ref<React.ElementRef<typeof DialogPrimitive.Overlay>>;
}

export interface DialogContentProps
  extends React.ComponentPropsWithoutRef<typeof DialogPrimitive.Content>,
    DialogProps {
  ref?: React.Ref<React.ElementRef<typeof DialogPrimitive.Content>>;
}

export interface DialogHeaderProps extends React.HTMLAttributes<HTMLDivElement> {
  ref?: React.Ref<HTMLDivElement>;
}

export interface DialogFooterProps extends React.HTMLAttributes<HTMLDivElement> {
  ref?: React.Ref<HTMLDivElement>;
}

export interface DialogTitleProps
  extends React.ComponentPropsWithoutRef<typeof DialogPrimitive.Title> {
  ref?: React.Ref<React.ElementRef<typeof DialogPrimitive.Title>>;
}

export interface DialogDescriptionProps
  extends React.ComponentPropsWithoutRef<typeof DialogPrimitive.Description> {
  ref?: React.Ref<React.ElementRef<typeof DialogPrimitive.Description>>;
}

// Root Dialog component
export const Dialog = DialogPrimitive.Root;

// Trigger component
export const DialogTrigger = DialogPrimitive.Trigger;

// Portal component for z-index management
export const DialogPortal = DialogPrimitive.Portal;

// Overlay with trust-building visual hierarchy
export function DialogOverlay({ className, ref, ...props }: DialogOverlayProps) {
  return (
    <DialogPrimitive.Overlay
      ref={ref}
      className={cn(
        // Base overlay with trust-building opacity
        'fixed inset-0 z-50 bg-background/80 backdrop-blur-sm',
        // Trust pattern: Smooth entrance reduces cognitive jarring
        'data-[state=open]:animate-in data-[state=closed]:animate-out',
        'data-[state=closed]:fade-out-0 data-[state=open]:fade-in-0',
        className
      )}
      {...props}
    />
  );
}

// Main Content component with intelligence patterns
export function DialogContent({
  className,
  children,
  trustLevel = 'medium',
  destructive = false,
  size = 'md',
  cognitiveComplexity = 'moderate',
  ref,
  ...props
}: DialogContentProps) {
  return (
    <DialogPortal>
      <DialogOverlay />
      <DialogPrimitive.Content
        ref={ref}
        className={cn(
          // Base styles with trust-building visual hierarchy
          'fixed left-[50%] top-[50%] z-50 translate-x-[-50%] translate-y-[-50%]',
          'bg-background border border-border rounded-lg shadow-lg',
          'focus:outline-none focus-visible:ring-2 focus-visible:ring-ring',

          // Trust-building: Enhanced borders for high-trust operations
          trustLevel === 'critical' && 'border-2 border-primary/20 shadow-xl',
          trustLevel === 'high' && 'border-primary/10 shadow-lg',

          // Destructive actions get visual warning indicators
          destructive && 'border-destructive/20 shadow-destructive/10',

          // Size variants for cognitive load management
          {
            'w-full max-w-sm': size === 'sm',
            'w-full max-w-md': size === 'md',
            'w-full max-w-lg': size === 'lg',
            'w-full max-w-2xl': size === 'xl',
            'w-[95vw] max-w-none h-[95vh]': size === 'full',
          },

          // Cognitive complexity affects spacing
          {
            'p-4 gap-3': cognitiveComplexity === 'simple',
            'p-6 gap-4': cognitiveComplexity === 'moderate',
            'p-8 gap-6': cognitiveComplexity === 'complex',
          },

          // Trust pattern: Smooth animations reduce cognitive jarring
          'transition-all',
          contextTiming.modal,
          contextEasing.modalEnter,
          'data-[state=open]:animate-in data-[state=closed]:animate-out',
          'data-[state=closed]:fade-out-0 data-[state=open]:fade-in-0',
          'data-[state=closed]:zoom-out-95 data-[state=open]:zoom-in-95',
          'data-[state=closed]:slide-out-to-left-1/2 data-[state=closed]:slide-out-to-top-[48%]',
          'data-[state=open]:slide-in-from-left-1/2 data-[state=open]:slide-in-from-top-[48%]',

          className
        )}
        {...props}
      >
        {children}
      </DialogPrimitive.Content>
    </DialogPortal>
  );
}

// Header with trust-building hierarchy
export function DialogHeader({ className, ref, ...props }: DialogHeaderProps) {
  return (
    <div
      ref={ref}
      className={cn(
        // Cognitive load optimization: Clear visual hierarchy
        'flex flex-col space-y-2 text-center sm:text-left',
        // Trust pattern: Adequate spacing prevents rushed decisions
        'mb-4',
        className
      )}
      {...props}
    />
  );
}

// Footer with action hierarchy and trust patterns
export function DialogFooter({ className, ref, ...props }: DialogFooterProps) {
