    trustLevel: 'high' as const,
    attentionPattern: 'hierarchical',
    description: 'Golden ratio proportions for natural visual hierarchy',
    responsive: {
      base: 'grid-cols-1',
      sm: 'sm:grid-cols-2',
      md: 'md:grid-cols-3',
      lg: 'lg:grid-cols-5', // Golden ratio relationship
      xl: 'xl:grid-cols-5',
    },
