        secondarySpan: 'lg:col-span-1 lg:row-span-1',
      },
      'feature-showcase': {
        template: 'lg:grid-cols-3 lg:grid-rows-3',
        primarySpan: 'lg:col-span-2 lg:row-span-2',
        secondarySpan: 'lg:col-span-1 lg:row-span-1',
      },
