} as const;

/**
 * Gap spacing mapping to Tailwind utilities and semantic tokens
 */
const GAP_CLASSES = {
  xs: 'gap-2', // 8px
  sm: 'gap-3', // 12px
  md: 'gap-4', // 16px
  lg: 'gap-6', // 24px
  xl: 'gap-8', // 32px
  comfortable: 'gap-[1.618rem]', // Golden ratio φ
  generous: 'gap-[2.618rem]', // Golden ratio φ²
} as const;

/**
 * Auto-fit minimum widths using design tokens
 */
const AUTO_FIT_WIDTHS = {
  sm: '200px', // Compact cards
  md: '280px', // Comfortable content
  lg: '360px', // Rich content
} as const;

/**
 * Cognitive load limits by viewport (Miller's Law implementation)
 */
const COGNITIVE_LIMITS = {
  mobile: 2, // Small screens, touch interface
  tablet: 4, // Medium screens, mixed interaction
  desktop: 6, // Large screens, precise interaction
  wide: 8, // Ultra-wide, professional use
} as const;

/**
 * Priority-based spanning for bento layouts
 * Automatically calculates grid item sizes based on content importance
 */
const PRIORITY_SPANS = {
  primary: {
    colSpan: 2,
    rowSpan: 2,
    cognitiveWeight: 8,
    attentionValue: 'highest',
  },
  secondary: {
    colSpan: 1,
    rowSpan: 1,
    cognitiveWeight: 4,
    attentionValue: 'medium',
  },
  tertiary: {
    colSpan: 1,
    rowSpan: 1,
    cognitiveWeight: 2,
    attentionValue: 'supporting',
  },
} as const;

/**
 * Grid Layout Intelligence Component
 *
 * Provides responsive grid layouts with embedded UX reasoning that AI agents
 * can understand and apply. Combines standard Tailwind utilities with
 * intelligent presets for systematic design decision-making.
 */
export function Grid({
  preset = 'linear',
  bentoPattern = 'editorial',
  columns,
  autoFit,
  gap = 'md',
  maxItems = 'auto',
  role = 'presentation',
  ariaLabel,
  ariaLabelledBy,
  as = 'div',
  onFocusChange,
  className,
  children,
  ref,
  ...props
}: GridProps) {
  const Component = as;

  // Get preset configuration
  const presetConfig = GRID_PRESETS[preset];

  // Build base grid classes
  const getGridClasses = () => {
    const classes: string[] = ['grid'];

    // Apply preset-specific responsive classes
    if (preset !== 'custom' && 'responsive' in presetConfig && presetConfig.responsive) {
      for (const cls of Object.values(presetConfig.responsive)) {
        if (cls) classes.push(cls);
      }
    }

    // Apply bento pattern classes
    if (preset === 'bento' && 'patterns' in presetConfig && presetConfig.patterns) {
      const pattern = presetConfig.patterns[bentoPattern];
      if (pattern) {
        classes.push(pattern.template);
      }
    }

    // Apply custom columns if specified
    if (preset === 'custom' && columns) {
      if (typeof columns === 'object' && 'base' in columns) {
        // Responsive columns
        if (columns.base) classes.push(`grid-cols-${columns.base}`);
        if (columns.sm) classes.push(`sm:grid-cols-${columns.sm}`);
        if (columns.md) classes.push(`md:grid-cols-${columns.md}`);
        if (columns.lg) classes.push(`lg:grid-cols-${columns.lg}`);
        if (columns.xl) classes.push(`xl:grid-cols-${columns.xl}`);
        if (columns['2xl']) classes.push(`2xl:grid-cols-${columns['2xl']}`);
      } else {
        // Simple columns
        if (columns === 'auto-fit' || columns === 'auto-fill') {
          // Handled by inline styles
        } else {
          classes.push(`grid-cols-${columns}`);
        }
      }
    }

    // Apply gap classes
    classes.push(GAP_CLASSES[gap]);

    return classes;
  };

  // Build inline styles for auto-sizing and custom gaps
  const getInlineStyles = (): React.CSSProperties => {
    const styles: React.CSSProperties = {};

    // Handle auto-fit patterns
    if (autoFit) {
      const minWidth = AUTO_FIT_WIDTHS[autoFit as keyof typeof AUTO_FIT_WIDTHS] || autoFit;
      styles.gridTemplateColumns = `repeat(auto-fit, minmax(${minWidth}, 1fr))`;
    }

    // Handle custom columns for auto patterns
    if (preset === 'custom' && typeof columns === 'string') {
      if (columns === 'auto-fit') {
        const minWidth = autoFit
          ? AUTO_FIT_WIDTHS[autoFit as keyof typeof AUTO_FIT_WIDTHS] || autoFit
          : '250px';
        styles.gridTemplateColumns = `repeat(auto-fit, minmax(${minWidth}, 1fr))`;
      } else if (columns === 'auto-fill') {
        const minWidth = autoFit
          ? AUTO_FIT_WIDTHS[autoFit as keyof typeof AUTO_FIT_WIDTHS] || autoFit
          : '250px';
        styles.gridTemplateColumns = `repeat(auto-fill, minmax(${minWidth}, 1fr))`;
      }
    }

    // Handle custom gaps that need inline styles (golden ratio gaps)
    if (gap === 'comfortable' || gap === 'generous') {
      if (gap === 'comfortable') {
        styles.gap = '1.618rem'; // Golden ratio φ
      } else if (gap === 'generous') {
        styles.gap = '2.618rem'; // Golden ratio φ²
      }
    }

    return styles;
  };

  return (
    <Component
      ref={ref as React.ForwardedRef<HTMLDivElement>}
      role={role}
      aria-label={ariaLabel}
      aria-labelledby={ariaLabelledBy}
      tabIndex={role === 'grid' ? 0 : undefined}
      className={cn(
        ...getGridClasses(),
        // Focus management for interactive grids
        role === 'grid' && [
          'focus-visible:outline-none',
          'focus-visible:ring-2',
          'focus-visible:ring-primary',
          'focus-visible:ring-offset-2',
        ],
        className
      )}
      style={{
        ...getInlineStyles(),
        ...props.style,
      }}
      {...props}
    >
      {children}
    </Component>
  );
}

/**
 * GridItem companion component with priority-based intelligence
 *
 * Provides semantic grid items with automatic sizing based on content
 * priority and accessibility integration for screen readers.
 */
export function GridItem({
  colSpan,
  rowSpan,
  priority,
  role = 'none',
  ariaLabel,
  focusable = false,
  as = 'div',
  className,
  children,
  ref,
  ...props
}: GridItemProps) {
  const Component = as;

  // Get priority-based spans for bento layouts
  const getPrioritySpans = () => {
    if (priority && PRIORITY_SPANS[priority]) {
      const spans = PRIORITY_SPANS[priority];
      return {
        colSpan: spans.colSpan,
        rowSpan: spans.rowSpan,
      };
    }
    return { colSpan, rowSpan };
  };

  const { colSpan: finalColSpan, rowSpan: finalRowSpan } = getPrioritySpans();

  // Build span classes
  const getSpanClasses = () => {
    const classes: string[] = [];

    // Handle responsive colSpan
    if (finalColSpan) {
      if (typeof finalColSpan === 'object' && 'base' in finalColSpan) {
        if (finalColSpan.base) classes.push(`col-span-${finalColSpan.base}`);
        if (finalColSpan.sm) classes.push(`sm:col-span-${finalColSpan.sm}`);
        if (finalColSpan.md) classes.push(`md:col-span-${finalColSpan.md}`);
        if (finalColSpan.lg) classes.push(`lg:col-span-${finalColSpan.lg}`);
        if (finalColSpan.xl) classes.push(`xl:col-span-${finalColSpan.xl}`);
        if (finalColSpan['2xl']) classes.push(`2xl:col-span-${finalColSpan['2xl']}`);
      } else {
        classes.push(`col-span-${finalColSpan}`);
      }
    }

    // Handle responsive rowSpan
    if (finalRowSpan) {
      if (typeof finalRowSpan === 'object' && 'base' in finalRowSpan) {
        if (finalRowSpan.base) classes.push(`row-span-${finalRowSpan.base}`);
        if (finalRowSpan.sm) classes.push(`sm:row-span-${finalRowSpan.sm}`);
        if (finalRowSpan.md) classes.push(`md:row-span-${finalRowSpan.md}`);
        if (finalRowSpan.lg) classes.push(`lg:row-span-${finalRowSpan.lg}`);
        if (finalRowSpan.xl) classes.push(`xl:row-span-${finalRowSpan.xl}`);
        if (finalRowSpan['2xl']) classes.push(`2xl:row-span-${finalRowSpan['2xl']}`);
      } else {
        classes.push(`row-span-${finalRowSpan}`);
      }
    }

    return classes;
  };

  return (
    <Component
      ref={ref as React.ForwardedRef<HTMLDivElement>}
      role={role}
      aria-label={ariaLabel}
      tabIndex={focusable ? 0 : undefined}
      className={cn(
        ...getSpanClasses(),
        // Focus management for interactive grid items
        focusable && [
          'focus-visible:outline-none',
          'focus-visible:ring-2',
          'focus-visible:ring-primary',
          'focus-visible:ring-offset-1',
        ],
        // Touch target compliance for interactive items
        role === 'gridcell' && [
          'min-h-[44px]', // WCAG AAA minimum
          'min-w-[44px]',
        ],
        className
      )}
      {...props}
    >
      {children}
    </Component>
  );
}

/**
 * Form input component with validation states and accessibility
 *
 * @registry-name input
 * @registry-version 0.1.0
 * @registry-status published
 * @registry-path components/ui/Input.tsx
 * @registry-type registry:component
 *
 * @cognitive-load 4/10 - Data entry with validation feedback requires user attention
 * @attention-economics State hierarchy: default=ready, focus=active input, error=requires attention, success=validation passed
 * @trust-building Clear validation feedback, error recovery patterns, progressive enhancement
 * @accessibility Screen reader labels, validation announcements, keyboard navigation, high contrast support
 * @semantic-meaning Type-appropriate validation: email=format validation, password=security indicators, number=range constraints
 *
 * @usage-patterns
 * DO: Always pair with descriptive Label component
 * DO: Use helpful placeholders showing format examples
 * DO: Provide real-time validation for user confidence
 * DO: Use appropriate input types for sensitive data
 * NEVER: Label-less inputs, validation only on submit, unclear error messages
 *
 * @design-guides
 * - Trust Building: https://rafters.realhandy.tech/docs/llm/trust-building
 * - Typography Intelligence: https://rafters.realhandy.tech/docs/llm/typography-intelligence
 * - Progressive Enhancement: https://rafters.realhandy.tech/docs/llm/progressive-enhancement
 *
 * @dependencies @rafters/design-tokens/motion
 *
 * @example
 * ```tsx
 * // Basic input with validation
 * <Input variant="error" validationMessage="Required field" showValidation />
 * 
 * // Sensitive data input
 * <Input type="password" sensitive />
 * 
 * // Real-time validation
 * <Input validationMode="live" variant="success" />
 * ```
 */
import { contextEasing, contextTiming } from '@rafters/design-tokens/motion';
import { cn } from '../lib/utils';

export interface InputProps extends React.InputHTMLAttributes<HTMLInputElement> {
  variant?: 'default' | 'error' | 'success' | 'warning';
  validationMode?: 'live' | 'onBlur' | 'onSubmit';
  sensitive?: boolean;
  showValidation?: boolean;
  validationMessage?: string;
  ref?: React.Ref<HTMLInputElement>;
}

export function Input({
  variant = 'default',
  validationMode = 'onBlur',
  sensitive = false,
  showValidation = false,
  validationMessage,
  className,
  type,
  ref,
  ...props
}: InputProps) {
  // Trust-building: Visual indicators for sensitive data
  const isSensitiveData = sensitive || type === 'password' || type === 'email';

  // Validation intelligence: Choose appropriate feedback timing
  const needsImmediateFeedback = variant === 'error' && validationMode === 'live';
  const hasValidationState = variant !== 'default';

  return (
    <div className="relative">
      <input
        ref={ref}
        type={type}
        className={cn(
          // Base styles - using semantic tokens with motor accessibility
          'flex h-10 w-full rounded-md border px-3 py-2 text-sm',
          'file:border-0 file:bg-transparent file:text-sm file:font-medium',
          'placeholder:text-muted-foreground',
          'focus-visible:outline-none focus-visible:ring-2 focus-visible:ring-ring focus-visible:ring-offset-2',
          'disabled:cursor-not-allowed disabled:opacity-disabled',
          'transition-all',
          contextTiming.hover,
          contextEasing.hover,
          'hover:opacity-hover',

          // Motor accessibility: Enhanced touch targets on mobile
          'min-h-[44px] sm:min-h-[40px]',

          // Trust-building: Visual indicators for sensitive data
          isSensitiveData && 'shadow-sm border-2',

          // Validation intelligence: Semantic variants with clear meaning
          {
            'border-input bg-background focus-visible:ring-primary': variant === 'default',
            'border-destructive bg-destructive/10 focus-visible:ring-destructive text-destructive-foreground':
              variant === 'error',
            'border-success bg-success/10 focus-visible:ring-success text-success-foreground':
              variant === 'success',
            'border-warning bg-warning/10 focus-visible:ring-warning text-warning-foreground':
              variant === 'warning',
          },

          // Enhanced styling for immediate feedback mode
          needsImmediateFeedback && 'ring-2 ring-destructive/20',

          className
        )}
        aria-invalid={variant === 'error'}
        aria-describedby={
          showValidation && validationMessage ? `${props.id || 'input'}-validation` : undefined
        }
        {...props}
      />

      {/* Validation message with semantic meaning */}
      {showValidation && validationMessage && (
        <div
          id={`${props.id || 'input'}-validation`}
          className={cn('mt-1 text-xs flex items-center gap-1', {
            'text-destructive': variant === 'error',
            'text-success': variant === 'success',
            'text-warning': variant === 'warning',
          })}
          role={variant === 'error' ? 'alert' : 'status'}
          aria-live={needsImmediateFeedback ? 'assertive' : 'polite'}
        >
          {/* Visual indicator for validation state */}
          {variant === 'error' && (
            <span
              className="w-3 h-3 rounded-full bg-destructive/20 flex items-center justify-center"
              aria-hidden="true"
            >
              <span className="w-1 h-1 rounded-full bg-destructive" />
            </span>
          )}
          {variant === 'success' && (
            <span
              className="w-3 h-3 rounded-full bg-success/20 flex items-center justify-center"
              aria-hidden="true"
            >
              <span className="w-1 h-1 rounded-full bg-success" />
            </span>
          )}
          {variant === 'warning' && (
            <span
              className="w-3 h-3 rounded-full bg-warning/20 flex items-center justify-center"
              aria-hidden="true"
            >
              <span className="w-1 h-1 rounded-full bg-warning" />
            </span>
          )}
          {validationMessage}
        </div>
      )}

      {/* Trust-building indicator for sensitive data */}
      {isSensitiveData && (
        <div
          className="absolute right-2 top-2 w-2 h-2 rounded-full bg-primary/30"
          aria-hidden="true"
        />
      )}
    </div>
  );
}

