        )}
        onClick={handleAnchorClick}
        aria-current={isActive ? 'page' : undefined}
        aria-disabled={disabled}
        aria-busy={loading}
        title={collapsed && showTooltip ? String(children) : undefined}
        {...(props as React.AnchorHTMLAttributes<HTMLAnchorElement>)}
