/**
 * Dialog Intelligence: cognitiveLoad=7, trustLevel=critical
 * Progressive confirmation REQUIRED for destructive actions
 * Trust-building patterns: clear consequences, escape hatches, familiar confirmation flows
 * Full patterns: .rafters/agent-instructions.md
 */
import * as DialogPrimitive from '@radix-ui/react-dialog';
import { forwardRef } from 'react';
import { cn } from '../lib/utils';

export interface DialogProps {
  // Trust-building intelligence
  trustLevel?: 'low' | 'medium' | 'high' | 'critical';
  destructive?: boolean;
  requireConfirmation?: boolean;

  // Cognitive load optimization
  cognitiveComplexity?: 'simple' | 'moderate' | 'complex';

  // Size variants for content hierarchy
  size?: 'sm' | 'md' | 'lg' | 'xl' | 'full';
}

export interface DialogContentProps
  extends React.ComponentPropsWithoutRef<typeof DialogPrimitive.Content>,
    DialogProps {}

export interface DialogHeaderProps extends React.HTMLAttributes<HTMLDivElement> {}

export interface DialogFooterProps extends React.HTMLAttributes<HTMLDivElement> {}

export interface DialogTitleProps
  extends React.ComponentPropsWithoutRef<typeof DialogPrimitive.Title> {}

export interface DialogDescriptionProps
  extends React.ComponentPropsWithoutRef<typeof DialogPrimitive.Description> {}

// Root Dialog component
export const Dialog = DialogPrimitive.Root;

// Trigger component
export const DialogTrigger = DialogPrimitive.Trigger;

// Portal component for z-index management
export const DialogPortal = DialogPrimitive.Portal;

// Overlay with trust-building visual hierarchy
export const DialogOverlay = forwardRef<
  React.ElementRef<typeof DialogPrimitive.Overlay>,
  React.ComponentPropsWithoutRef<typeof DialogPrimitive.Overlay>
>(({ className, ...props }, ref) => (
  <DialogPrimitive.Overlay
    ref={ref}
    className={cn(
      // Base overlay with trust-building opacity
      'fixed inset-0 z-50 bg-background/80 backdrop-blur-sm',
      // Trust pattern: Smooth entrance reduces cognitive jarring
      'data-[state=open]:animate-in data-[state=closed]:animate-out',
      'data-[state=closed]:fade-out-0 data-[state=open]:fade-in-0',
      className
    )}
    {...props}
  />
));
DialogOverlay.displayName = DialogPrimitive.Overlay.displayName;

// Main Content component with intelligence patterns
export const DialogContent = forwardRef<
  React.ElementRef<typeof DialogPrimitive.Content>,
  DialogContentProps
>(
  (
    {
      className,
      children,
      trustLevel = 'medium',
      destructive = false,
      size = 'md',
      cognitiveComplexity = 'moderate',
      ...props
    },
    ref
  ) => (
    <DialogPortal>
      <DialogOverlay />
      <DialogPrimitive.Content
        ref={ref}
        className={cn(
          // Base styles with trust-building visual hierarchy
          'fixed left-[50%] top-[50%] z-50 translate-x-[-50%] translate-y-[-50%]',
          'bg-background border border-border rounded-lg shadow-lg',
          'focus:outline-none focus-visible:ring-2 focus-visible:ring-ring',

          // Trust-building: Enhanced borders for high-trust operations
          trustLevel === 'critical' && 'border-2 border-primary/20 shadow-xl',
          trustLevel === 'high' && 'border-primary/10 shadow-lg',

          // Destructive actions get visual warning indicators
          destructive && 'border-destructive/20 shadow-destructive/10',

          // Size variants for cognitive load management
          {
            'w-full max-w-sm': size === 'sm',
            'w-full max-w-md': size === 'md',
            'w-full max-w-lg': size === 'lg',
            'w-full max-w-2xl': size === 'xl',
            'w-[95vw] max-w-none h-[95vh]': size === 'full',
          },

          // Cognitive complexity affects spacing
          {
            'p-4 gap-3': cognitiveComplexity === 'simple',
            'p-6 gap-4': cognitiveComplexity === 'moderate',
            'p-8 gap-6': cognitiveComplexity === 'complex',
          },

          // Trust pattern: Smooth animations reduce cognitive jarring
          'duration-200 data-[state=open]:animate-in data-[state=closed]:animate-out',
          'data-[state=closed]:fade-out-0 data-[state=open]:fade-in-0',
          'data-[state=closed]:zoom-out-95 data-[state=open]:zoom-in-95',
          'data-[state=closed]:slide-out-to-left-1/2 data-[state=closed]:slide-out-to-top-[48%]',
          'data-[state=open]:slide-in-from-left-1/2 data-[state=open]:slide-in-from-top-[48%]',

          className
        )}
        {...props}
      >
        {children}
      </DialogPrimitive.Content>
    </DialogPortal>
  )
);
DialogContent.displayName = DialogPrimitive.Content.displayName;

// Header with trust-building hierarchy
export const DialogHeader = forwardRef<HTMLDivElement, DialogHeaderProps>(
  ({ className, ...props }, ref) => (
    <div
      ref={ref}
      className={cn(
        // Cognitive load optimization: Clear visual hierarchy
        'flex flex-col space-y-2 text-center sm:text-left',
        // Trust pattern: Adequate spacing prevents rushed decisions
        'mb-4',
        className
      )}
      {...props}
    />
  )
);
DialogHeader.displayName = 'DialogHeader';

// Footer with action hierarchy and trust patterns
export const DialogFooter = forwardRef<HTMLDivElement, DialogFooterProps>(
  ({ className, ...props }, ref) => (
    <div
      ref={ref}
      className={cn(
        // Trust pattern: Actions flow from low to high commitment (left to right)
        'flex flex-col-reverse sm:flex-row sm:justify-end sm:space-x-2',
        // Motor accessibility: Enhanced spacing for touch targets
        'space-y-2 sm:space-y-0 pt-4',
        // Cognitive load: Clear separation from content
        'border-t border-border/50 mt-6',
        className
      )}
      {...props}
    />
  )
);
DialogFooter.displayName = 'DialogFooter';

// Title with semantic hierarchy
export const DialogTitle = forwardRef<
  React.ElementRef<typeof DialogPrimitive.Title>,
  DialogTitleProps
>(({ className, ...props }, ref) => (
  <DialogPrimitive.Title
    ref={ref}
    className={cn(
      // Cognitive load: Clear title hierarchy
      'text-lg font-semibold leading-none tracking-tight',
      // Trust pattern: Titles establish context and confidence
      'text-foreground',
      className
    )}
    {...props}
  />
));
DialogTitle.displayName = DialogPrimitive.Title.displayName;

// Description with trust-building clarity
export const DialogDescription = forwardRef<
  React.ElementRef<typeof DialogPrimitive.Description>,
  DialogDescriptionProps
>(({ className, ...props }, ref) => (
  <DialogPrimitive.Description
    ref={ref}
    className={cn(
      // Trust pattern: Clear explanation reduces uncertainty
      'text-sm text-muted-foreground leading-relaxed',
      // Cognitive load: Comfortable reading line height
      'max-w-none',
      className
    )}
    {...props}
  />
));
DialogDescription.displayName = DialogPrimitive.Description.displayName;

// Close button with escape hatch accessibility
export const DialogClose = DialogPrimitive.Close;

import { forwardRef } from 'react';
import { cn } from '../lib/utils';

export interface InputProps extends React.InputHTMLAttributes<HTMLInputElement> {
  variant?: 'default' | 'error' | 'success' | 'warning';
  validationMode?: 'live' | 'onBlur' | 'onSubmit';
  sensitive?: boolean;
  showValidation?: boolean;
  validationMessage?: string;
}

export const Input = forwardRef<HTMLInputElement, InputProps>(
  (
    {
      variant = 'default',
      validationMode = 'onBlur',
      sensitive = false,
      showValidation = false,
      validationMessage,
      className,
      type,
      ...props
    },
    ref
  ) => {
    // Trust-building: Visual indicators for sensitive data
    const isSensitiveData = sensitive || type === 'password' || type === 'email';

    // Validation intelligence: Choose appropriate feedback timing
    const needsImmediateFeedback = variant === 'error' && validationMode === 'live';
    const hasValidationState = variant !== 'default';

    return (
      <div className="relative">
        <input
          ref={ref}
          type={type}
          className={cn(
            // Base styles - using semantic tokens with motor accessibility
            'flex h-10 w-full rounded-md border px-3 py-2 text-sm',
            'file:border-0 file:bg-transparent file:text-sm file:font-medium',
            'placeholder:text-muted-foreground',
            'focus-visible:outline-none focus-visible:ring-2 focus-visible:ring-ring focus-visible:ring-offset-2',
            'disabled:cursor-not-allowed disabled:opacity-disabled',
            'transition-all duration-200',
            'hover:opacity-hover',

            // Motor accessibility: Enhanced touch targets on mobile
            'min-h-[44px] sm:min-h-[40px]',

            // Trust-building: Visual indicators for sensitive data
            isSensitiveData && 'shadow-sm border-2',

            // Validation intelligence: Semantic variants with clear meaning
            {
              'border-input bg-background focus-visible:ring-primary': variant === 'default',
              'border-destructive bg-destructive/10 focus-visible:ring-destructive text-destructive-foreground':
                variant === 'error',
              'border-success bg-success/10 focus-visible:ring-success text-success-foreground':
                variant === 'success',
              'border-warning bg-warning/10 focus-visible:ring-warning text-warning-foreground':
                variant === 'warning',
            },

            // Enhanced styling for immediate feedback mode
            needsImmediateFeedback && 'ring-2 ring-destructive/20',

            className
          )}
          aria-invalid={variant === 'error'}
          aria-describedby={
            showValidation && validationMessage ? `${props.id || 'input'}-validation` : undefined
          }
          {...props}
        />

        {/* Validation message with semantic meaning */}
        {showValidation && validationMessage && (
          <div
            id={`${props.id || 'input'}-validation`}
            className={cn('mt-1 text-xs flex items-center gap-1', {
              'text-destructive': variant === 'error',
              'text-success': variant === 'success',
              'text-warning': variant === 'warning',
            })}
            role={variant === 'error' ? 'alert' : 'status'}
            aria-live={needsImmediateFeedback ? 'assertive' : 'polite'}
          >
            {/* Visual indicator for validation state */}
            {variant === 'error' && (
              <span
                className="w-3 h-3 rounded-full bg-destructive/20 flex items-center justify-center"
                aria-hidden="true"
              >
                <span className="w-1 h-1 rounded-full bg-destructive" />
              </span>
            )}
            {variant === 'success' && (
              <span
                className="w-3 h-3 rounded-full bg-success/20 flex items-center justify-center"
                aria-hidden="true"
              >
                <span className="w-1 h-1 rounded-full bg-success" />
              </span>
            )}
            {variant === 'warning' && (
              <span
                className="w-3 h-3 rounded-full bg-warning/20 flex items-center justify-center"
                aria-hidden="true"
              >
                <span className="w-1 h-1 rounded-full bg-warning" />
              </span>
            )}
            {validationMessage}
          </div>
        )}

        {/* Trust-building indicator for sensitive data */}
        {isSensitiveData && (
          <div
            className="absolute right-2 top-2 w-2 h-2 rounded-full bg-primary/30"
            aria-hidden="true"
          />
        )}
      </div>
    );
  }
);

Input.displayName = 'Input';

import * as LabelPrimitive from '@radix-ui/react-label';
import { forwardRef } from 'react';
import { cn } from '../lib/utils';

export interface LabelProps extends React.ComponentPropsWithoutRef<typeof LabelPrimitive.Root> {
  required?: boolean;
  importance?: 'critical' | 'standard' | 'optional';
  context?: 'form' | 'descriptive' | 'action';
  validationState?: 'error' | 'warning' | 'success' | 'default';
  helpText?: string;
  semantic?: boolean;
}

export const Label = forwardRef<React.ElementRef<typeof LabelPrimitive.Root>, LabelProps>(
  (
    {
      className,
      required,
      importance = 'standard',
      context = 'form',
      validationState = 'default',
      helpText,
      semantic = true,
      children,
      ...props
    },
    ref
  ) => (
    <div className={cn('space-y-1', semantic && 'semantic-label-container')}>
      <LabelPrimitive.Root
        ref={ref}
        className={cn(
          // Base label styling
          'text-sm leading-none text-foreground',
          'peer-disabled:cursor-not-allowed peer-disabled:opacity-disabled',

          // Importance-based visual hierarchy
          {
            'font-semibold': importance === 'critical',
            'font-medium': importance === 'standard',
            'font-normal opacity-75': importance === 'optional',
          },

          // Context-specific styling
          {
            'cursor-pointer': context === 'form',
            'cursor-default': context === 'descriptive',
            'cursor-pointer hover:text-accent-foreground': context === 'action',
          },

          // Validation state colors
          {
            'text-destructive': validationState === 'error',
            'text-warning': validationState === 'warning',
            'text-success': validationState === 'success',
          },

          className
        )}
        {...props}
      >
        {children}
        {required && (
          <span
            className={cn(
              'ml-1',
              validationState === 'error' ? 'text-destructive' : 'text-destructive'
            )}
            aria-label="required field"
          >
            *
          </span>
        )}
        {importance === 'optional' && !required && (
          <span className="ml-1 text-muted-foreground text-xs font-normal">(optional)</span>
        )}
      </LabelPrimitive.Root>

      {helpText && (
        <p
          className={cn('text-xs text-muted-foreground', {
            'text-destructive': validationState === 'error',
            'text-warning': validationState === 'warning',
            'text-success': validationState === 'success',
          })}
          role={validationState === 'error' ? 'alert' : 'status'}
          aria-live={validationState === 'error' ? 'assertive' : 'polite'}
        >
          {helpText}
        </p>
      )}
    </div>
  )
);

Label.displayName = LabelPrimitive.Root.displayName;

import { forwardRef } from 'react';
import { cn } from '../lib/utils';

// Base layout props interface
export interface LayoutProps extends React.HTMLAttributes<HTMLDivElement> {}

// Container component for width constraints and padding
export interface ContainerProps extends React.HTMLAttributes<HTMLDivElement> {
  variant?: 'reading' | 'golden' | 'wide' | 'full';
}

export const Container = forwardRef<HTMLDivElement, ContainerProps>(
  ({ variant = 'golden', className, ...props }, ref) => {
    const variants = {
      reading: 'container-reading',
      golden: 'container-golden',
      wide: 'max-w-7xl mx-auto px-phi-1',
      full: 'w-full px-phi-1',
    };

    return <div ref={ref} className={cn(variants[variant], className)} {...props} />;
  }
);
Container.displayName = 'Container';

// Reading layout for text-heavy content (F-pattern optimized)
export const ReadingLayout = forwardRef<HTMLDivElement, LayoutProps>(
  ({ className, ...props }, ref) => (
    <div ref={ref} className={cn('reading-layout', className)} {...props} />
  )
);
ReadingLayout.displayName = 'ReadingLayout';

// Action layout for conversion-focused content (Z-pattern optimized)
export const ActionLayout = forwardRef<HTMLDivElement, LayoutProps>(
  ({ className, ...props }, ref) => (
    <div ref={ref} className={cn('action-layout', className)} {...props} />
  )
);
ActionLayout.displayName = 'ActionLayout';

// Content sidebar layout for main content + complementary info
export const ContentSidebar = forwardRef<HTMLDivElement, LayoutProps>(
  ({ className, ...props }, ref) => (
    <div ref={ref} className={cn('content-sidebar', className)} {...props} />
  )
);
ContentSidebar.displayName = 'ContentSidebar';

// Application layout for header + sidebar + main content
export const AppLayout = forwardRef<HTMLDivElement, LayoutProps>(({ className, ...props }, ref) => (
