/**
 * Type definitions for masky-js
 *
 * masky-js is a lightweight input masking library that auto-initializes
 * from the data-mask attribute on input elements.
 *
 * @see https://github.com/eduardovillao/masky-js
 */
declare module 'masky-js' {
  const masky: {
    init: () => void;
  };
  export default masky;
}
